import { useState,useEffect } from "react";
export const TypingHeading = () => {
    const [text, setText] = useState('');
    const [textIndex, setTextIndex] = useState(0);
  
    const fullText = "Welcome! my Name is Arbab Ahmad Khan Am a Front End Developer"; // Your desired text
  
    const typeNextCharacter = () => {
      if (textIndex < fullText.length) {
        setText(text + fullText.charAt(textIndex));
        setTextIndex(textIndex + 1);
      }
    };
  
    useEffect(() => {
      if (textIndex < fullText.length) {
        const typingTimeout = setTimeout(typeNextCharacter, 100); // Adjust typing speed here
        return () => clearTimeout(typingTimeout);
      }
    }, [textIndex]);
  
    return (
      <h1>{text}</h1>
      
    );
  };
